import { Component, OnInit } from '@angular/core';
import { AuthService } from '../auth.service';
import * as SpotifyWebApi from 'spotify-web-api-js';
import { ActivatedRoute } from '@angular/router';
@Injectable({
  providedIn: 'root'
})
@Component({
  selector: 'app-disk',
  templateUrl: './disk.component.html',
  styleUrls: ['./disk.component.css']
})
export class DiskComponent implements OnInit{


  constructor(private authService: AuthService, private route: ActivatedRoute) {
  }
  ngOnInit() {
    // @ts-ignore
    const token = this.route.snapshot.fragment.split('&')
        .find(fragment => fragment.includes('access_token'))
        .split('=')[1];

    // Guardar el token de acceso en el servicio de autenticación
    this.authService.setAccessToken(token);

  }
  topTracks(){
    const topTracks: any[] = [];
    const token = this.authService.getAccessToken();

    // Crear una instancia de SpotifyWebApi con el token de acceso
    const spotifyApi = new SpotifyWebApi();
    spotifyApi.setAccessToken(token);

    // Obtener las canciones más escuchadas
    spotifyApi.getMyTopTracks({ limit: 10 })
        .then((data) => {
          console.log(data);
          this.topTracks = data.items;
        }, (err) => {
          console.error(err);
        });
  }
}
