import {Component, OnInit} from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-disk',
  templateUrl: './disk.component.html',
  styleUrls: ['./disk.component.css']
})
export class DiskComponent implements OnInit{
  accessToken: string | null = null;

  constructor(private http: HttpClient, private authService: AuthService) {
    const urlParams = new URLSearchParams(window.location.hash.substring(1));
    this.accessToken = urlParams.get('access_token');
  }

  ngOnInit() {
    localStorage.setItem('spotify_token', token);
  }

  getTopTracks() {
    // Obtener el token de acceso del servicio AuthService
    const accessToken = this.accessToken;

    // Construir las opciones de la solicitud
    const httpOptions = {
      headers: new HttpHeaders({
        'Authorization': `Bearer ${accessToken}`
      })
    };

    // Obtener la fecha actual
    const now = new Date();

    // Obtener la fecha de hace un mes
    const lastMonth = new Date();
    lastMonth.setMonth(now.getMonth() - 1);

    // Formatear las fechas para incluirlas en la solicitud
    const nowStr = now.toISOString().substring(0, 10);
    const lastMonthStr = lastMonth.toISOString().substring(0, 10);

    // Construir la URL de la solicitud
    const url = `https://api.spotify.com/v1/me/top/tracks?time_range=short_term&limit=10&offset=0`;

    // Realizar la solicitud a la API de Spotify
    this.http.get(url, httpOptions).subscribe(
        (response: any) => {
          console.log(response.items);
          // Aquí puedes procesar la respuesta y mostrar las canciones en el componente
        },
        (error: any) => {
          console.error(error);
        }
    );
  }

  logout() {
    this.authService.logout();
  }

}
