import { Injectable } from '@angular/core';
import SpotifyWebApi from 'spotify-web-api-js';

@Injectable({
    providedIn: 'root'
})
export class AuthService {

    private spotifyApi: SpotifyWebApi.SpotifyWebApiJs;

    constructor() {
        this.spotifyApi = new SpotifyWebApi();
    }

    login(): void {
        const client_id = '933d3430c2e6442eac18add3a796497d';
        const redirect_uri = 'http://localhost:4200/disk';

        window.location.href = `https://accounts.spotify.com/authorize?client_id=${client_id}&redirect_uri=${redirect_uri}&response_type=token&show_dialog=true`;
    }

    setAccessToken(token: string): void {
        this.spotifyApi.setAccessToken(token);
    }

    getApi(): SpotifyWebApi.SpotifyWebApiJs {
        return this.spotifyApi;
    }
}
