import {Component, Injectable, OnInit} from '@angular/core';
import { AuthService } from '../auth.service';
import SpotifyWebApi from 'spotify-web-api-js';
import { ActivatedRoute } from '@angular/router';
import { from } from 'rxjs';
import { Observable } from 'rxjs';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { catchError, map } from 'rxjs/operators';
@Injectable({
  providedIn: 'root'
})
@Component({
  selector: 'app-disk',
  templateUrl: './disk.component.html',
  styleUrls: ['./disk.component.css']
})
export class DiskComponent implements OnInit{
  private spotifyApi: SpotifyWebApi.SpotifyWebApiJs;
  usuario: string | undefined = "";
  private apiUrl = 'https://api.spotify.com/v1/me';
  constructor(private authService: AuthService, private route: ActivatedRoute, private http: HttpClient) {
    this.spotifyApi = this.authService.getApi();
  }

  ngOnInit(): void {
    // @ts-ignore
    const token = this.route.snapshot.fragment.split('&')
        .find(fragment => fragment.includes('access_token'))
        .split('=')[1];

    // Guardar el token de acceso en el servicio de autenticación
    this.authService.setAccessToken(token);
    this.getDefault2();

  }

  async getMyTopTracks() {
    this.authService.getApi().getMyTopTracks(function (err: any, data: any) {
      if (err) console.error(err);
      else console.log('Artist albums', data);
    });
  }

  getElvis(){
    this.authService.getApi().getArtistAlbums('43ZHCT0cAZBISjO8DG9PnE', function (err: any, data: any) {
      if (err) console.error(err);
      else console.log('Artist albums', data);
    });
  }

  getMe(){
    this.authService.getApi().getMe().then((data) => {
      console.log(data)
    }).catch((error) => {
      console.error(error);
    });
  }

  getDefault(){
   var a = this.authService.getApi().getMe()
    //a = JSON.parse(JSON.stringify(this.authService.getApi().getMe()))
   a.then((data) =>{
     console.log(data)
     this.usuario =  data.display_name
    })
  }

  getDefault2(){
    this.authService.getApi().getMyTopTracks({
      time_range: "medium_term",
      limit:10
    }).then((response) => {
      console.log(response.items)
    })
  }




}
