import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable, of } from 'rxjs';
import { catchError, map, tap } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  private apiURL = 'https://accounts.spotify.com/authorize';
  private clientId = '933d3430c2e6442eac18add3a796497d'; // reemplaza con tu propio cliente ID
  private redirectUri = 'http://localhost:4200/disk'; // reemplaza con tu propia URI de redireccionamiento
  private scopes = [
    'user-read-private',
    'user-read-email',
    'user-library-read'
  ];

  constructor(private http: HttpClient) {}

  login() {
    const url = `${this.apiURL}?client_id=${this.clientId}&redirect_uri=${this.redirectUri}&scope=${this.scopes.join('%20')}&response_type=token`;
    window.location.href = url;
  }

  logout() {
    // elimina los tokens de acceso y actualización de la sesión actual
  }

  isLoggedIn() {
    // comprueba si hay tokens de acceso y actualización en la sesión actual
  }

  // @ts-ignore
  getUserInfo(): Observable<any> {
    // obtén la información del usuario actual utilizando los tokens de acceso y actualización
  }

}
