import { Component } from '@angular/core';
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { OAuthService } from 'angular-oauth2-oidc';

@Injectable({
  providedIn: 'root'
})

@Component({
  selector: 'app-principa',
  templateUrl: './principa.component.html',
  styleUrls: ['./principa.component.css']
})
export class PrincipaComponent {
  constructor(
      private http: HttpClient,
      private oauthService: OAuthService
  ) {
    this.configureOAuth();
  }

  private configureOAuth() {
    this.oauthService.configure({
      issuer: 'https://accounts.spotify.com',
      clientId: 'TU_CLIENT_ID',
      redirectUri: 'TU_REDIRECT_URI',
      scope: 'user-read-private user-read-email',
      responseType: 'code',
      tokenEndpoint: 'https://accounts.spotify.com/api/token',
      oidc: false
    });
    // @ts-ignore
    this.oauthService.tokenValidationHandler = null;
    this.oauthService.setStorage(localStorage);
  }

  login() {
    this.oauthService.initCodeFlow();
  }

  logout() {
    this.oauthService.logOut();
  }

  isLoggedIn() {
    return this.oauthService.hasValidAccessToken();
  }

  getAccessToken() {
    return this.oauthService.getAccessToken();
  }

  getUserInfo() {
    return this.http.get('https://api.spotify.com/v1/me');
  }
}
