import { Component } from '@angular/core';
import html2canvas from 'html2canvas';



@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  downloadImage() {
    alert("a")
    const container = document.getElementById('img') as HTMLInputElement
    html2canvas(container).then(function(canvas) {
      // Obtener la URL de datos de la imagen
      const dataURL = canvas.toDataURL("image/png");

      // Crear un enlace para descargar la imagen
      const link = document.createElement("a");
      link.download = "imagen.png";
      link.href = dataURL;

      // Agregar el enlace al DOM y hacer clic en él para descargar la imagen
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    });
  }

}
<div style="position: relative; left: 0; top: 0;" id="img">
    <div class="album-cover eye">
        <div class="vinyl-cover"></div>
        <div class="album-info">
            <h1>Nombre del Álbum</h1>
            <h2>Artista</h2>
            <div class="song-list">
                <div class="column">
                    <ul>
                        <li>Canción 1</li>
                        <li>Canción 2</li>
                        <li>Canción 3</li>
                        <li>Canción 4</li>
                        <li>Canción 5</li>
                    </ul>
                </div>
                <div class="column">
                    <ul>
                        <li>Canción 6</li>
                        <li>Canción 7</li>
                        <li>Canción 8</li>
                        <li>Canción 9</li>
                        <li>Canción 10</li>
                    </ul>
                </div>
            </div>
        </div>
    </div>
    <img src="assets/ase.png" class="heaven"/>
</div>

<div>
    <button (click)="downloadImage()">Descargar imagen</button>
</div>
