import { Component } from '@angular/core';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

}



<div style="position: relative; left: 0; top: 0;">
    <img src="assets/bunny.jpeg" class='eye'/>
    <img src="assets/ase.png" class="heaven"/>
    <div style="position: absolute; top: 50%; left: 50%; transform: translate(-50%, -50%); z-index: 2; color: white; text-align: center;">
        <h1 style="font-size: 3rem;">Título del disco</h1>
        <h2 style="font-size: 2rem;">Nombre del artista</h2>
    </div>
</div>
