import { Component } from '@angular/core';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

}
<div class="vinyl-box">
    <div class="vinyl-top"></div>
    <div class="vinyl">
        <div class="label">
            <img src="https://vinylify.com/wp-content/uploads/2017/06/lagoonscover.jpg" alt="Nombre del álbum">
        </div>
    </div>
    <div class="vinyl-bottom"></div>
</div>
